class Form extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            description: "",
            cityZip: "",
            loading: true
        }

        this.onSubmit = this.onSubmit.bind(this)
    }


    onSubmit(event) {
        event.preventDefault();

        console.log("Search button clicked")
        this.setState({
            description: this.refs.description.value.trim(),
            cityZip: this.refs.cityZip.value.trim()
        }, console.log(this.state))

    }
    render() {
        return (
            <div className="container" style={styles.formContainer}>
                <div className="input-group mb-3">
                    <input type="text" ref="description" className="form-control" placeholder="Description ex: Java, Ruby, Javascript" aria-label="Username" aria-describedby="basic-addon1" />
                </div>
                <div className="input-group mb-3">
                    <div className="col-xs-6">
                        <input type="text" ref="cityZip" style={styles.cityInput} className="form-control" placeholder="City Name or Zipcode" aria-label="Username" aria-describedby="basic-addon1" />
                    </div>
                    <button type="button" onClick={this.onSubmit} style={styles.buttonStyle} className="btn btn-primary">Search</button>
                </div>

            </div>


        );
    };

};

const styles = {
    formContainer: {
        padding: '10px',
        border: '1px solid black'
    },
    buttonStyle: {
        marginLeft: '20px'
    }

}


export default Form;
